import React, { useState, useEffect } from 'react';
import { BrowserRouter, Link, Switch, Route } from 'react-router-dom';
import logo from './logo.svg';
import './App.css';
import Nav from './Nav';
import ManageCourse from './manage_course'

function App() {
  const [currentTime, setCurrentTime] = useState(0);

  useEffect(() => {
    fetch('/api/time').then(res => res.json()).then(data => {
      setCurrentTime(data.time);
    });
  }, []);
  return (
    <div className="App">
      <h1>Random University</h1>
      <Nav />
      <ManageCourse />
    </div>
  );
}

function Home (){
  const [currentTime, setCurrentTime] = useState(0);

  useEffect(() => {
    fetch('/api/time').then(res => res.json()).then(data => {
      setCurrentTime(data.time);
    });
  }, []);
  return(
    <div>
    <img src={logo} className="App-logo" alt="logo" />
    <p>
      Edit <code>src/App.js</code> and save to reload.
    </p>
    <a
      className="App-link"
      href="https://reactjs.org"
      target="_blank"
      rel="noopener noreferrer"
    >
      Learn React
    </a>
    <p>The current time is {currentTime}.</p>
    </div>
  )
}

function ManageCourses(){
  return(
    <div>
      <h2>Manage Courses</h2>
      <BrowserRouter>
      <div>
        <li><Link className="App-link" to="/manage_courses/view_courses">View Courses</Link></li>
        <li><Link className="App-link" to="/manage_courses/add_a_course">Add a Course</Link></li>   
      </div>
          <Switch>
            <Route path="/manage_courses/view_courses">
                  <ViewCourses />
            </Route>
            <Route path="/manage_courses/add_a_course">
                  <AddCourse />
            </Route>
          </Switch>
      </BrowserRouter>

    </div>
  )
}

function ViewCourses(){
  return(
    <h3>View Courses</h3>
  )
}

function AddCourse(){
  return(
    <h3>Add a Course</h3>
  )
}

export default App;
