import React, { useState, useEffect } from 'react';
import { BrowserRouter, Link, Switch, Route } from 'react-router-dom';
import logo from './logo.svg';
import './App.css';

function App() {
  const [currentTime, setCurrentTime] = useState(0);

  useEffect(() => {
    fetch('/api/time').then(res => res.json()).then(data => {
      setCurrentTime(data.time);
    });
  }, []);
  return (
    <div className="App">
      <header className="App-header">
        <BrowserRouter>
          <div>
            <Link className="App-link" to="/">Home</Link>
            &nbsp;|&nbsp;
            <Link className="App-link" to="/manage_courses">Manage Courses</Link>
            &nbsp;|&nbsp;
            <Link className="App-link" to="/manage_students">Manage Students</Link>
            &nbsp;|&nbsp;
            <Link className="App-link" to="/manage_instructors">Manage Instructors</Link>
            <br></br>
            <Link className="App-link" to="/
            manage_buildings">Manage Building</Link>
            &nbsp;|&nbsp;
            <Link className="App-link" to="/
            manage_time">Manage Class Time</Link>
          </div>
          <Switch>
            <Route exact path="/">
                <Home />
            </Route>
            <Route path="/view_courses">
                <ViewCourses />
            </Route>
          </Switch>
        </BrowserRouter>
      </header>
    </div>
  );
}

function Home (){
  const [currentTime, setCurrentTime] = useState(0);

  useEffect(() => {
    fetch('/api/time').then(res => res.json()).then(data => {
      setCurrentTime(data.time);
    });
  }, []);
  return(
    <div>
    <img src={logo} className="App-logo" alt="logo" />
    <p>
      Edit <code>src/App.js</code> and save to reload.
    </p>
    <a
      className="App-link"
      href="https://reactjs.org"
      target="_blank"
      rel="noopener noreferrer"
    >
      Learn React
    </a>
    <p>The current time is {currentTime}.</p>
    </div>
  )
}

function ViewCourses(){

}


export default App;
