import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Link, Switch, Route } from 'react-router-dom';
import './App.css';
// import CourseTable from "./course_table";

function ViewCourse(){

    useEffect(()=>{
        fetchCourses();
    },[]);

    const[courses, setCourses] = useState([]);

    const fetchCourses = async () => {
        // console.log(e)
        const data = await fetch("/api/view_course",{
            method: 'POST'});
        const courses_data = await data.json();
        console.log(courses_data.results)
        // console.log(courses_data.items);
        setCourses(courses_data.results)
    };


    const UserForm = props => {
        const [user, setUser] = useState(props.user)
        const form = useRef(null)}
    const onFormSubmit = async (e) => {
        e.preventDefault()
        const data = new FormData(form.current)
        await fetch("/api/view_course",{
            method: 'POST', body:data});
            const courses_data = await data.json();
            console.log(courses_data.results)

    };

    const initialFormData = Object.freeze({
      });
    const [formData, updateFormData] = React.useState(initialFormData);

        const handleChange = (e) => {
          updateFormData({
            ...formData,

            // Trimming any whitespace
            [e.target.name]: e.target.value.trim()
          });
        };

        const handleSubmit = async(e) => {
          e.preventDefault()
        //   console.log(formData);
          const submitdata = new FormData(formData)
          const data = await fetch("/api/view_course",{
            method: 'POST', body:submitdata});
            const courses_data = await data.json();
            console.log(courses_data.results)
        // console.log(courses_data.items);

          // ... submit to API or something
        };


    return(
        <div>
            <h3>Course Information</h3>
            <form ref={form} className="pl-5 pr-5 row" method="post" action="/api/view_course" onSubmit={onFormSubmit}>
                <div className="col">
                    Course_ID:
                    <input type="text" className="form-control"  placeholder="Course ID" id="sections.section_id" name="sections.section_id" />
                </div>
                <div className="col">
                    Division:
                    <input type="text" className="form-control" placeholder="Division" id="division" name="dept" />
                </div>
                <div className="col">
                    Instructor_ID:
                    <input type="text" className="form-control" placeholder="Instructor ID" name="sections.instructor_id"/>
                </div>
                <div className="col">
                    Building_ID
                    <input type="text" className="form-control" placeholder="Building ID" name="rooms.building_id"/>
                </div>

                <button className="btn btn-lg btn-primary btn-block" type="submit">Search</button>
            </form>
            <div>
                {courseComponent(courses)}
            </div>
            {/* {courses.map(course=>(
                <div className="border border-dark mt-3 mb-0 ml-5 mr-5 rounded one_course">
                <div className="row mt-3 mb-0 ml-5 mr-5 text-left">
                    <p className="col border border-dark font-weight-bold" key={course.course_name}>{course.course_name}</p>
                    <p className="col border border-dark rounded-left" key={course.course_id}>Course ID: {course.course_id}</p>
                    <p className="col border border-dark" key={course.section_id}>Section ID: {course.section_id}</p>
                    <p className="col border border-dark" key={course.building_id}>Building_id: {course.building_id}</p>
                    <p className="col border border-dark" key={course.room_name}>Room: {course.room_name}</p>
                    <p className="col border border-dark" key={course.instructor_name}>Instructor: {course.instructor_lname},{course.instructor_fname}</p>
                    <p className="col border border-dark" key={course.instructor_id}>Instructor_ID: {course.instructor_id}</p>
                </div>
                    <p className="mt-0 mb-5 ml-5 mr-5 text-left font-italic" key={course.descript}>Description: {course.descript}</p>
                </div> */}
            {/* ))} */}
        </div>
    );

    function courseComponent (courses) { 
        return(
            <div>
            {courses.map(course=>(
                <div className="border border-dark mt-3 mb-0 ml-5 mr-5 rounded one_course">
                    <div className="row mt-3 mb-0 ml-5 mr-5 text-left">
                        <p className="col border border-dark font-weight-bold" key={course.course_name}>{course.course_name}</p>
                        <p className="col border border-dark rounded-left" key={course.course_id}>Course ID: {course.course_id}</p>
                        <p className="col border border-dark" key={course.section_id}>Section ID: {course.section_id}</p>
                        <p className="col border border-dark" key={course.building_id}>Building_id: {course.building_id}</p>
                        <p className="col border border-dark" key={course.room_name}>Room: {course.room_name}</p>
                        <p className="col border border-dark" key={course.instructor_name}>Instructor: {course.instructor_lname},{course.instructor_fname}</p>
                        <p className="col border border-dark" key={course.instructor_id}>Instructor_ID: {course.instructor_id}</p>
                    </div>
                    <p className="mt-0 mb-5 ml-5 mr-5 text-left font-italic" key={course.descript}>Description: {course.descript}</p>
                </div>
                ))}
            </div>
        )
    }
}

export default ViewCourse;
