import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Link, Switch, Route } from 'react-router-dom';
import './App.css';


function ViewCourse(){

    useEffect(()=>{
        fetchCourses();
    },[]);

    const[courses, setCourses] = useState([]);

    const fetchCourses = async () => {
        const data = await fetch("/api/view_course");
        const courses_data = await data.json();
        // console.log(courses_data.items);
        setCourses(courses_data.items)

    };


    return(
        <div>
            <h1 className="text-primary">Course Information</h1>
            <form className="pl-5 pr-5" method="POST" action="/api/view_course">
                {/* <h2 className="form-signin-heading">Please sign in</h2> */}
                <div className="form-row">
                    <div class="col">
                        <label className="text-left" for="inputName">Email</label>
                        <input type="text" class="form-control" placeholder="Name" id="inputName" />
                    </div>
                    <div class="col">
                        <label for="division">Division</label>
                        <input type="text" class="form-control" placeholder="Division" id="division" />
                    </div>
                </div>
                <input className="form-control" type="text" id="instructor" placeholder="Instructor ID" />
                <input className="form-control" type="password" name="course_code" placeholder="CourseCode" />
                {/* <label className="checkbox"></label> */}
                <button className="btn btn-lg btn-primary btn-block" type="submit">Search</button>
            </form>
            {courses.map(course=>(
                <h1 key={course.section_id}>{course.section_id}</h1>
            ))}
        </div>
    );
}

export default ViewCourse;
