import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Link, Switch, Route } from 'react-router-dom';
import './App.css';


function ViewCourse(){

    useEffect(()=>{
        fetchCourses();
    },[]);

    const[courses, setCourses] = useState([]);

    const fetchCourses = async () => {
        const data = await fetch("/api/view_course");
        const courses_data = await data.json();
        // console.log(courses_data.items);
        setCourses(courses_data.items)

    };


    return(
        <div>
            <h1 className="text-primary">Course Information</h1>
            <form className="pl-5 pr-5" method="post" action="/api/view_course">
                <div className="row">
                    <label className="text-left" for="course_id">Course_ID:</label>
                    <input type="text" className="form-control" placeholder="Course ID" id="course_id" />
                </div>
                <div className="row">
                    <label for="division">Division: </label>
                    <input type="text" className="form-control" placeholder="Division" id="division" />
                </div>
                <div className="row">
                    <label for="instructor">Instructor_ID: </label>
                    <input type="text" className="form-control" placeholder="Instructor ID" id="instructor" />
                </div>
                <div className="row">
                    <label for="building">Building_ID </label>
                    <input type="text" className="form-control" placeholder="Building ID" id="building" />
                </div>

                <button className="btn btn-lg btn-primary btn-block" type="submit">Search</button>
            </form>
            {courses.map(course=>(
                <h1 key={course.section_id}>{course.section_id}</h1>
            ))}
        </div>
    );
}

export default ViewCourse;
