import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Link, Switch, Route } from 'react-router-dom';
import './App.css';


function ViewCourse(){

    useEffect(()=>{
        fetchCourses();
    },[]);

    const[courses, setCourses] = useState([]);

    const fetchCourses = async () => {
        const data = await fetch("/api/view_course");
        const courses_data = await data.json();
        // console.log(courses_data.items);
        setCourses(courses_data.items)

    };


    return(
        <div>
            <h1 className="text-primary">Course Information</h1>
            <div className="search_form">
            <form class="form-signin" method="POST" action="URL_OF_REST">
            <h2 class="form-signin-heading">Please sign in</h2>
            <input class="form-control" type="text" required name="email" placeholder="Email address" />
            <input class="form-control" type="password" required name="password" placeholder="Password" />
            <label class="checkbox"></label>
            <button class="btn btn-lg btn-primary btn-block" type="submit">Sign in</button>
            </form>
            </div>
            {courses.map(course=>(
                <h1 key={course.section_id}>{course.section_id}</h1>
            ))}
        </div>
    );
}

export default ViewCourse;
