function ViewCourse(){

    useEffect(()=>{
        fetchCourses();
    },[]);

    const[courses, setCourses] = useState([]);

    const fetchCourses = async () => {
        const data = await fetch("/api/view_course",{
            method: 'POST'});
        const courses_data = await data.json();
        console.log(courses_data.results)
        // console.log(courses_data.items);
        setCourses(courses_data.results)
    };

   function courseComponent (data) { 
