                        <p className="col border border-dark" key={course.section_id}>Section ID: {course.section_id}</p>
                        <p className="col border border-dark" key={course.building_id}>Building_id: {course.building_id}</p>
                        <p className="col border border-dark" key={course.room_name}>Room: {course.room_name}</p>
                        <p className="col border border-dark" key={course.instructor_name}>Instructor: {course.instructor_lname},{course.instructor_fname}</p>
                        <p className="col border border-dark" key={course.instructor_id}>Instructor_ID: {course.instructor_id}</p>
                    </div>
                    <p className="mt-0 mb-5 ml-5 mr-5 text-left font-italic" key={course.descript}>Description: {course.descript}</p>
                </div>
                ))}
            </div>
        )
    }

